/**
 * ver1.1.2
 * 
 * update
 *      add expired time: close the vote when time is up-to-date
 *      add list voters who can vote
 *          if the list is left empty, everyone can join the vote
 *          otherwise, only the ones in the list can cast vote
 *      get ballot of an address
 * 
 *-----------------------------------------------------
 *  ver1.1.0
 * 
 * update
 *      VotFor
 *      change the way managing the ballot
 *          voteOf => listVotersOf
 *      getCountOf => getCountOf
 * add new:
 *  property: 
 *      owner
 *      listVotersOf
 *  method:
 *      getListVoterOf
 * remove:
 *      voteOf
 *      getCountOf
 * 
 * -----------------------------------------------------
 * 
 * * ver1.1.0
 * 
 * update
 *      VotFor
 *      change the way managing the ballot
 *          voteOf => listVotersOf
 *      getCountOf => getCountOf
 * add new:
 *  property: 
 *      owner
 *      listVotersOf
 *  method:
 *      getListVoterOf
 * remove:
 *      voteOf
 *      getCountOf

 * 
 * -----------------------------------------------------
 * ver1.1.1
 * 
 * update
 *      add algorithm check 1 address - 1 vote
 * add new:
 *  property: 
 *      voted
 *  method:
 *      (none)
 * remove:
 *      (none)
 * -----------------------------------------------------
 *  * ver1.1.0
 * 
 * update
 *      VotFor
 *      change the way managing the ballot
 *          voteOf => listVotersOf
 *      getCountOf => getCountOf
 * add new:
 *  property: 
 *      owner
 *      listVotersOf
 *  method:
 *      getListVoterOf
 * remove:
 *      voteOf
 *      getCountOf
 * 
 * -----------------------------------------------------
 * ver1
 * 
 * add option when call the contract
 * vote for an option
 * convert option(bytes32) to string
 * get option vote
 * only available option can be voted
 * 
 * 

 * 
 */
pragma solidity ^0.4.18;

contract votingVer1_1_4 {

  address owner;
  bytes32[] opt;
  //mapping(bytes32 => uint) voteOf;
  mapping(bytes32 => address[]) listVotersOf;
  mapping(address => bool) voted;
  mapping(address => bytes32) ballotOf;
  address[] voters;

  function votingVer1_1_4(bytes32[] _opt, address[] _voters) public {
    owner = msg.sender;
    opt = _opt;
    voters = _voters;
  }

  function addOption(bytes32 _n) public {
    opt.push(_n);
  }

  function addVoters(address _addr) public {
    voters.push(_addr);
  }

  function getOptions() public view returns(bytes32[]) {
    return opt;
  }

  function getListVoters() public view returns(address[]) {
    return voters;
  }

  function getOptionByIndex(uint _i) private view returns(string) {
    return bytes32ToString(opt[_i]);
  }


  function bytes32ToString(bytes32 x) public pure returns(string) {
    bytes memory bytesString = new bytes(32);
    uint charCount = 0;
    for (uint j = 0; j < 32; j++) {
      byte char = byte(bytes32(uint(x) * 2 ** (8 * j)));
      if (char != 0) {
        bytesString[charCount] = char;
        charCount++;
      }
    }
    bytes memory bytesStringTrimmed = new bytes(charCount);
    for (j = 0; j < charCount; j++) {
      bytesStringTrimmed[j] = bytesString[j];
    }
    return string(bytesStringTrimmed);
  }
  /* 
   modifier validopt(bytes32 _n){
       for(uint i=0; i < opt.length; i++){
           if(opt[i] == _n){
               return true;
           }   
       }
       return false;
       _;
       
   }
   */
  function vailableOption(bytes32 _n) private view returns(bool) {
    for (uint i = 0; i < opt.length; i++) {
      if (opt[i] == _n) {
        return true;
      }
    }
    return false;
  }

  function vailableVoter(address _addr) private view returns(bool) {
    for (uint i = 0; i < voters.length; i++) {
      if (voters[i] == _addr) {
        return true;
      }
    }
    return false;
  }

  function VoteFor(bytes32 _option) public {
    require(vailableOption(_option));
    require(voted[msg.sender] == false);
    if (voters.length != 0) {
      require(vailableVoter(msg.sender) == true);
    }
    ballotOf[msg.sender] = _option;
    listVotersOf[_option].push(msg.sender);
    voted[msg.sender] = true;
  }


  function getVoteOf(address _addr) public view returns(bytes32) {
    if (voted[_addr] == true) {
      return ballotOf[_addr];
    }
    return 0x00;
  }

  function getCountOf(bytes32 _option) public constant returns(uint) {
    require(vailableOption(_option));
    return listVotersOf[_option].length;
  }

  function getListVoterOf(bytes32 _id) public view returns(address[]) {
    return listVotersOf[_id];
  }


}
